<?php
if ( ! defined( 'ABSPATH' ) ) exit;

$slides = [];
if ( have_rows( 'content_image_slides' ) ) {
	while ( have_rows( 'content_image_slides' ) ) { the_row();
		$image   = get_sub_field( 'image' );
		$content = get_sub_field( 'content' );
		$slides[] = [ 'image' => $image, 'content' => $content ];
	}
}

if ( ! empty( $slides ) ) : ?>
<section class="content-image-slider">
	<div class="container">
		<div class="row col-12">
			<div class="image-slider-controls">
				<div class="image-slider-prev">
					<svg xmlns="http://www.w3.org/2000/svg" width="22" height="22" viewBox="0 0 22 22" fill="none">
						<path d="M13.75 17.875L6.875 11L13.75 4.125" stroke="black" stroke-width="1.83333" stroke-linecap="round" stroke-linejoin="round"/>
					</svg>
				</div>
				<div class="image-slider-pagination"></div>
				<div class="image-slider-next">
					<svg xmlns="http://www.w3.org/2000/svg" width="22" height="22" viewBox="0 0 22 22" fill="none">
						<path d="M8.25 4.125L15.125 11L8.25 17.875" stroke="black" stroke-width="1.83333" stroke-linecap="round" stroke-linejoin="round"/>
					</svg>
				</div>
			</div>
		</div>
			<div class="images-slider col-lg-8 col-12 swiper-container">
				<div class="swiper-wrapper">
					<?php foreach ( $slides as $s ) :
						$img_id = $s['image']['ID'] ?? 0;
						$alt    = $s['image']['alt'] ?? '';
					?>
					<div class="swiper-slide">
						<?php if ( $img_id ) : ?>
							<?php echo wp_get_attachment_image( $img_id, 'full', false, array( 'alt' => esc_attr( $alt ) ) ); ?>
						<?php endif; ?>
					</div>
					<?php endforeach; ?>
				</div>
			</div>

			<div class="content-slider col-lg-6 col-12 swiper-container dark-gold ">
				<div class="swiper-wrapper">
					<?php foreach ( $slides as $s ) : ?>
						<div class="swiper-slide">
							<article class="content-item">
								<?php echo wp_kses_post( $s['content'] ); ?>
							</article>
						</div>
					<?php endforeach; ?>
				</div>
			</div>

	</div>
	
